
-- Create the table with an id column using UUID
CREATE TABLE carbon_emmision (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4 (),
    Country VARCHAR(50),
    CO2_kg_per_kWh DECIMAL(5, 3)
);

